mapper.select:
SELECT COUNT(id)
FROM   ACCOUNT_BOOK_ENTRY
WHERE  group_id = #{groupId} AND id = #{entryId}

mapper.insert:
INSERT INTO ACCOUNT_BOOK_ENTRY (
       id
     , user_id
     , group_id
     , category_id
     , content
     , description
     , price
     , date
     , type
     , method
     , created_at
)
VALUES (
       #{id}
     , #{userId}
     , #{groupId}
     , #{categoryId}
     , #{content}
     , #{description}
     , #{price}
     , #{date}
     , #{type}
     , #{method}
     , CURRENT_TIMESTAMP
)

mapper.select:
SELECT
       #{date}
     , IFNULL(SUM(CASE WHEN type = 'INCOME' THEN price ELSE 0 END), 0)
     , IFNULL(SUM(CASE WHEN type = 'EXPENSE' THEN price ELSE 0 END), 0)
FROM ACCOUNT_BOOK_ENTRY
WHERE group_id = #{groupId} AND date = #{date}

mapper.update:
UPDATE ACCOUNT_BOOK_ENTRY
SET
       category_id = #{request.categoryId}
     , content     = #{request.content}
     , description = #{request.description}
     , price       = #{request.price}
     , type        = #{request.type}
     , method      = #{request.method}
WHERE  id = #{entryId} AND group_id = #{groupId}
