mapper.insert:
INSERT INTO USER (
       id
     , email
     , name
     , nickname
     , phone
     , role
     , status
     , type
     , rtk
     , last_login_at
     , created_at
     , updated_at
) VALUES (
       #{id}
     , #{email}
     , #{name}
     , #{nickname}
     , #{phone}
     , #{role}
     , #{status}
     , #{type}
     , null
     , null
     , CURRENT_TIMESTAMP
     , CURRENT_TIMESTAMP
)

mapper.update:
UPDATE USER
SET
       rtk           = #{rtk}
     , last_login_at = CURRENT_TIMESTAMP
     , updated_at    = CURRENT_TIMESTAMP
WHERE  id = #{id}

mapper.insert:
INSERT INTO USER_LOGIN_HISTORY (
