mapper.insert:
INSERT INTO "USER" (
       id
     , email
     , name
     , nickname
     , phone
     , role
     , status
     , type
     , rtk
     , last_login_at
     , created_at
     , updated_at
)
VALUES (
       #{id}
     , #{email}
     , #{name}
     , #{nickname}
     , #{phone}
     , #{role}
     , #{status}
     , #{type}
     , null
     , null
     , NOW()
     , NOW()
)

mapper.update:
UPDATE "USER"
SET    rtk           = #{rtk}
     , last_login_at = NOW()
     , updated_at    = NOW()
WHERE id = #{id}

